var express = require("express");
var app = express();
var bodyParser = require("body-parser");

 var campgrounds = [
        {name:"Samon Creek", image:"data:image/jpeg;base64,"},
        {name:"Granet Hill", image:"https://imagesvc.timeincapp.com/v3/mm/image?url=https%3A%2F%2Fcdn-image.travelandleisure.com%2Fsites%2Fdefault%2Ffiles%2Fstyles%2F1600x1000%2Fpublic%2F1443561122%2FCAMPING0915-Glacier-National-Park.jpg%3Fitok%3D6gQxpDuT&w=700&q=85"} ,  
        {name:"Mountain Goat's Rest", image:"https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTjJN7afQX9VFewqt-FWa0CenfQ7vMFJthr2kWfDNak4UwzsAo1Ag"},
        {name:"Samon Creek", image:"data:image/jpeg;base64,"},
        {name:"Granet Hill", image:"https://imagesvc.timeincapp.com/v3/mm/image?url=https%3A%2F%2Fcdn-image.travelandleisure.com%2Fsites%2Fdefault%2Ffiles%2Fstyles%2F1600x1000%2Fpublic%2F1443561122%2FCAMPING0915-Glacier-National-Park.jpg%3Fitok%3D6gQxpDuT&w=700&q=85"} ,  
        {name:"Mountain Goat's Rest", image:"https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTjJN7afQX9VFewqt-FWa0CenfQ7vMFJthr2kWfDNak4UwzsAo1Ag"},
        {name:"Samon Creek", image:"data:image/jpeg;base64,"},
        {name:"Granet Hill", image:"https://imagesvc.timeincapp.com/v3/mm/image?url=https%3A%2F%2Fcdn-image.travelandleisure.com%2Fsites%2Fdefault%2Ffiles%2Fstyles%2F1600x1000%2Fpublic%2F1443561122%2FCAMPING0915-Glacier-National-Park.jpg%3Fitok%3D6gQxpDuT&w=700&q=85"} ,  
        {name:"Mountain Goat's Rest", image:"https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTjJN7afQX9VFewqt-FWa0CenfQ7vMFJthr2kWfDNak4UwzsAo1Ag"}
    ];

app.use(bodyParser.urlencoded({extended: true}));
app.set("view engine", "ejs");

app.get("/", function(req, res){
    res.render("landing");
});
// here are the campgrounds page
app.get("/campgrounds", function(req, res){
    res.render("campgrounds", {campgrounds: campgrounds});
});
//app.post wil send  the data from form to the campground page
app.post("/campgrounds", function(req, res){
    //get data from form and add it to array
    var name = req.body.name;
    var image = req.body.image;
    var newCampground = {name: name, image: image};
    campgrounds.push(newCampground);
    //redirect back to campgrounds
    res.redirect("/campgrounds");
});
// here is the form to add a new camp ground to the app.post
app.get("/campgrounds/new", function(req, res) {
    res.render("new");
});

app.listen(process.env.PORT, process.env.IP, function(){
    console.log("Running !!!");
});